import {
  FormControl,
  Grid,
  InputLabel,
  MenuItem,
  Select,
  TextField,
  Typography,
  Button,
} from "@material-ui/core";
import React, { useEffect, useState } from "react";
import { useLocation } from "react-router";

export default function FormCreateEdit({
  classes,
  onSubmit,
  book,
  handleChange,
  handleSelected,
  categorias,
  precio,
  titulo,
  editorial,
  autor,
  selected,
}) {
  const location = useLocation();
  const [edit, setEdit] = useState(
    location.pathname === "/admin/product/edit/:id" ? true : false,

    useEffect(() => {
      setEdit();
    }, [location.pathname])
  );
  return (
    <form className={classes.form} onSubmit={onSubmit}>
      <Typography variant="h4" style={{ margin: 20 }}>
        Modificá el libro
      </Typography>
      <Grid
        container
        spacing={4}
        container
        direction="row"
        justify="space-between"
        alignItems="center"
      >
        <Grid item xs={12}>
          <TextField
            name="titulo"
            label=""
            variant="outlined"
            fullWidth
            id="firstName"
            label={edit ? "Título: " + book.titulo : null}
            autoFocus
            value={titulo}
            onChange={handleChange}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            variant="outlined"
            fullWidth
            id="lastName"
            label={edit ? "Autor: " + book.autor : null}
            name="autor"
            value={autor}
            onChange={handleChange}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <FormControl
            variant="filled"
            className={classes.formControl}
            fullWidth
          >
            <InputLabel id="demo-simple-select-filled-label">
              {selected ? selected : null}
            </InputLabel>
            <Select
              labelId="demo-simple-select-filled-label"
              label="Categoría"
              name="categoria"
              id="demo-simple-select-filled"
              value={selected}
              onChange={handleChange}
            >
              {categorias &&
                categorias.map((categ) => {
                  <MenuItem value="">
                    <em>{categ && categ.name}</em>
                  </MenuItem>;
                  return (
                    <MenuItem
                      value={selected}
                      onClick={() => handleSelected(categ.name, categ._id)}
                    >
                      {categ.name}
                    </MenuItem>
                  );
                })}
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            name="editorial"
            variant="outlined"
            fullWidth
            id="editorial"
            label={edit ? "Editorial: " + book.editorial : null}
            autoComplete="email"
            onChange={handleChange}
            value={editorial}
          />
        </Grid>
        <Grid Item xs={12} style={{ padding: 20 }}>
          <TextField
            variant="outlined"
            placeholder={edit ? "Reseña:" + book.reseqna : null}
            fullWidth
            label={"Reseña:"}
            multiline
            rows={6}
          />
        </Grid>
        <Grid item xs={6} sm={3} justify="center">
          <TextField
            name="precio"
            variant="outlined"
            label={edit ? "Precio: $" + book.precio : null}
            margin="normal"
            id="precio"
            autoComplete="current-password"
            value={precio}
            onChange={handleChange}
          />
        </Grid>
      </Grid>
      <Grid
        container
        spacing={4}
        container
        direction="row"
        justify="center"
        alignItems="center"
      >
        <Grid item xs={6} sm={3} justify="center">
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="secondary"
            className={classes.submit}
          >
            Enviar Info
          </Button>
        </Grid>
      </Grid>
    </form>
  );
}
