import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { setBook } from "../state/book";

import { useHistory } from "react-router-dom";
import { setCarrito } from "../state/carrito";
import { Grid, Paper, Typography, ButtonBase, Button } from "@material-ui/core";
import useStyles from "../utils/stylesSIngleCard";

export default function SingleCard({ id }) {
  const history = useHistory()
  console.log(id);
  const classes = useStyles();
  const dispatch = useDispatch();

  // const history = useHistory();

  const libro = useSelector((store) => store.book);
  const carrito = useSelector((store) => store.carrito);

  useEffect(() => {
    dispatch(setBook(id));
    localStorage.setItem("book", JSON.stringify(carrito));
  }, [carrito]);

  const handleClick = (libro) => {
    dispatch(setCarrito(libro));
    // localStorage.setItem("book", JSON.stringify(carrito));
    // return history.push(`/shop`);
  };

  return (
    <div className={classes.root}>
      <div style={{ marginTop: "50px" }}>
        <Paper className={classes.paper}>
          <Typography gutterBottom variant="h3" align="center">
            {libro.titulo}
          </Typography>
          <Grid container>
            <Grid container style={{ width: 500 }} justify="space-around">
              <ButtonBase className={classes.image}>
                <img className={classes.img} alt="complex" src={libro.imagen} />
              </ButtonBase>
            </Grid>
            <Grid item xs={12} sm container>
              <Grid item xs container direction="column" spacing={2}>
                <Grid item xs>
                  <Typography variant="body2" gutterBottom>
                    {libro.reseqna}
                  </Typography>
                  <Typography variant="body2" color="textSecondary">
                    Autor:{libro.autor}
                  </Typography>
                  <Typography variant="body2" color="textSecondary">
                    $ {libro.precio}
                  </Typography>

                  <Typography variant="body2" color="textSecondary">
                    Categoria:
                    {(libro.categoria && libro.categoria.name) ||
                      " Sin categoría"}
                  </Typography>
                </Grid>
                <Grid item>
                  <Typography
                    variant="body2"
                    style={{ cursor: "pointer" }}
                    onClick={() => handleClick(libro)}
                  >
                    agregar a carrito
                  </Typography>
                  <Typography
                    variant="body2"
                    style={{ cursor: "pointer" }}
                    onClick={() => history.push("/")}
                  >
                    agregar reseña
                  </Typography>
                  <Typography
                    variant="body2"
                    style={{ cursor: "pointer" }}
                    onClick={() => handleClick(libro)}
                  >
                    ver reseñas
                  </Typography>
                </Grid>
              </Grid>
            </Grid>
          </Grid>
        </Paper>
      </div>
    </div>
  );
}
