import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setBooks } from "../state/books";
import { useHistory } from "react-router-dom";

import Navbar from "./Navbar";
import useStyles from "../utils/stylesHome";

import {
  Button,
  Typography,
  Card,
  CardActions,
  CardContent,
  CardMedia,
  Container,
  Grid,
  createMuiTheme,
  ThemeProvider,
} from "@material-ui/core";

const theme = createMuiTheme({
  typography: {
    fontFamily: ["Syne Mono", "cursive"].join(","),
  },
});

export default function Home({ changeMode }) {
  const classes = useStyles();

  const history = useHistory();
  const dispatch = useDispatch();
  const books = useSelector((state) => state.books);

  const handleClick = (id) => {
    // dispatch(setBook(id));  604ac406ed088172721807d9
    return history.push(`/SingleCard/${id}`);
  };

  useEffect(() => {
    dispatch(setBooks());
  }, []);

  return (
    <ThemeProvider theme={theme}>
      <React.Fragment>
        <Navbar changeMode={changeMode} />
        <main className="color">
          <div className={classes.heroContent}>
            <Container maxWidth="sm">
              <Typography
                component="h1"
                variant="h1"
                align="center"
                color="textPrimary"
                gutterBottom
              >
                BIENVENIDOS A E-BOOKS
              </Typography>
              <Typography
                variant="h5"
                align="center"
                color="textSecondary"
                paragraph
              >
                NUESTRA MEJOR SELECCION DE LIBROS PARA VOS
              </Typography>
            </Container>
          </div>
          <Container className={classes.cardGrid} maxWidth="md">
            <Grid container spacing={4}>
              {books.map((card) => (
                <Grid item key={card._id} xs={12} sm={6} md={4}>
                  <Card className={classes.card}>
                    <CardMedia
                      style={{ padding: 20 }}
                      className={classes.cardMedia}
                      image={card.imagen}
                      title="Image title"
                    />
                    <CardContent className={classes.cardContent}>
                      <Typography gutterBottom variant="h5" component="h2">
                        {card.titulo}
                      </Typography>
                      <Typography>PRECIO: ${card.precio}</Typography>
                    </CardContent>
                    <CardActions>
                      <Button
                        size="small"
                        color="primary"
                        onClick={() => handleClick(card._id)}
                      >
                        DETALLE
                      </Button>

                      <Button
                        size="small"
                        color="primary"
                        onClick={() => history.push("/shop")}
                      >
                        COMPRAR
                      </Button>

                      <Button
                        size="small"
                        color="primary"
                        onClick={() => history.push("/shop")}
                      >
                        Agregar al carrito
                      </Button>
                    </CardActions>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Container>
        </main>
      </React.Fragment>
    </ThemeProvider>
  );
}
