import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";

import { useHistory } from "react-router-dom";
import { setCarrito } from "../state/carrito";
import { setBook } from "../state/book";
import {setCarritoLogin, setComprar} from "../state/comprar";

import {
  Grid,
  Paper,
  Typography,
  ButtonBase,
  Button,
  ButtonGroup,
} from "@material-ui/core";
import useStyles from "../utils/stylesSIngleCard";

export default function SingleCard({ id }) {
  const history = useHistory();
  console.log(id);
  const classes = useStyles();
  const dispatch = useDispatch();

  // const history = useHistory();

  const libro = useSelector((store) => store.book);
  const carrito = useSelector((store) => store.carrito);

  useEffect(() => {
    dispatch(setBook(id));
    localStorage.setItem("book", JSON.stringify(carrito));
  }, [carrito]);

  const handleClick = (libro) => {
    dispatch(setCarrito(libro));
    let carritoCompras = libro
     let userId= localStorage.getItem("userId")
      dispatch(setCarritoLogin({userId, carritoCompras}));
    };



  return (
    <div className={classes.root}>
      <div style={{ marginTop: "50px" }}>
        <Paper className={classes.paper}>
          <Typography gutterBottom variant="h3" align="center">
            {libro.titulo}
          </Typography>
          <Grid container>
            <Grid container style={{ width: 500 }} justify="space-around">
              <ButtonBase className={classes.image}>
                <img className={classes.img} alt="complex" src={libro.imagen} />
              </ButtonBase>
            </Grid>
            <Grid item xs={12} sm container>
              <Grid item xs container direction="column" spacing={2}>
                <Grid item xs>
                  <Typography variant="body2" gutterBottom>
                    {libro.reseqna}
                  </Typography>
                  <Typography variant="body2" color="textSecondary">
                    Autor:{libro.autor}
                  </Typography>
                  <Typography variant="body2" color="textSecondary">
                    $ {libro.precio}
                  </Typography>

                  <Typography variant="body2" color="textSecondary">
                    Categoria:
                    {(libro.categoria && libro.categoria.name) ||
                      " Sin categorÃ­a"}
                  </Typography>
                </Grid>

                <ButtonGroup
                  variant="text"
                  color="primary"
                  aria-label="text primary button group"
                >
                  <Button>Agregar a carrito</Button>
                  <Button
                    onClick={() => history.push(`/singlecard/${id}/review`)}
                  >
                    Agregar review
                  </Button>
                  <Button
                    onClick={() => history.push(`/singlecard/${id}/reviews`)}
                  >
                    ver reviews
                  </Button>
                </ButtonGroup>

                {/* <Grid item>
                  <Typography
                    variant="body2"
                    style={{ cursor: "pointer" }}
                    onClick={() => handleClick(libro)}
                  >
                    agregar a carrito
                  </Typography>
                  <Typography
                    variant="body2"
                    style={{ cursor: "pointer" }}
                    onClick={() => history.push(`/singlecard/${id}/review`)}
                  >
                    agregar review
                  </Typography>
                  <Typography
                    variant="body2"
                    style={{ cursor: "pointer" }}
                    onClick={() => history.push(`/singlecard/${id}/reviews`)}
                  >
                    ver reseÃ±as
                  </Typography>
                </Grid> */}
              </Grid>
            </Grid>
          </Grid>
        </Paper>
      </div>
    </div>
  );
}
