import {
  Avatar,
  Grid,
  IconButton,
  List,
  ListItem,
  ListItemAvatar,
  ListItemSecondaryAction,
  ListItemText,
  Typography,
  Tooltip,
  Button,
} from "@material-ui/core";

import { Edit, Delete } from "@material-ui/icons";
import MenuBookIcon from "@material-ui/icons/MenuBook";
import { useStyles } from "../utils/stylesAdminUsers";
import axios from "axios";
import React, { useEffect, useState } from "react";

export default function ReviewsBook({ id }) {
  const [reviews, setReviews] = useState();

  useEffect(() => {
    axios.get(`/api/review/${id}`).then(({ data }) => setReviews(data))
  }, []);

  const classes = useStyles();

  return (
    <div>
      <Grid container direction="row" justify="center" alignItems="center">
        <Grid item xs={10} style={{ padding: 20 }}>
          <div className={classes.demo}>
            <List>
              {reviews &&
                reviews.map((review, i) => (
                    <ListItem key={i}>
                    {/* <ListItemAvatar>
                      <Avatar>
                        <MenuBookIcon />
                      </Avatar>
                    </ListItemAvatar> */}
                      <Typography>{review.user.nombre} {": "}</Typography>

                    <ListItemText primary={review.texto} />
                    <Tooltip title="EDITAR">
                      <ListItemSecondaryAction
                        style={{ paddingRight: 65 }}
                        // onClick={() => handleEditBook(book._id)}
                      >
                        <IconButton edge="end" aria-label="add-admin">
                          <Edit />
                        </IconButton>
                      </ListItemSecondaryAction>
                    </Tooltip>
                    <Tooltip title="ELIMINAR">
                      <ListItemSecondaryAction
                        // onClick={() => handleDelete(book._id)}
                      >
                        <IconButton edge="end" aria-label="delete">
                          <Delete />
                        </IconButton>
                      </ListItemSecondaryAction>
                    </Tooltip>
                  </ListItem>
                ))}
            </List>
          </div>
        </Grid>
      </Grid>
    </div>
  );
}
