import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import {
  Grid,
  Paper,
  Typography,
  ButtonBase,
  Button,
  ButtonGroup,
} from "@material-ui/core";

// import useStyles from "../utils/stylesSIngleCard";

import { makeStyles } from "@material-ui/core/styles";
import Dialog from "@material-ui/core/Dialog";
import ListItemText from "@material-ui/core/ListItemText";
import ListItem from "@material-ui/core/ListItem";
import List from "@material-ui/core/List";
import Divider from "@material-ui/core/Divider";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import Slide from "@material-ui/core/Slide";

// import {
//   Grid,
//   List,
//   ListItem,
//   ListItemText,
//   Typography,
// } from "@material-ui/core";

import { useStyles } from "../utils/stylesAdminUsers";

import axios from "axios";
import { useState } from "react";

import { useHistory } from "react-router-dom";
import { setCarrito } from "../state/carrito";
import { setBook } from "../state/book";
import { setCarritoLogin, setComprar } from "../state/comprar";

// const useStyles = makeStyles((theme) => ({
//   appBar: {
//     position: "relative",
//   },
//   title: {
//     marginLeft: theme.spacing(2),
//     flex: 1,
//   },
// }));
// const useStyles = makeStyles((theme) => ({
//   appBar: {
//     position: "relative",
//   },
//   title: {
//     marginLeft: theme.spacing(2),
//     flex: 1,
//   },
//   typography: {
//     subtitle: {
//       fontSize: 12,
//     },
//   },
//   root: {
//     display: "flex",
//     "& > * + *": {
//       marginLeft: theme.spacing(2),
//     },
//     minHeight: "100vh",

//     flexGrow: 1,
//     maxWidth: 800,
//   },
//   demo: {
//     backgroundColor: theme.palette.background.paper,
//   },
// }));

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

export default function SingleCard({ id }) {
  const [reviews, setReviews] = useState();

  const classes = useStyles();
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
    axios.get(`/api/review/${id}`).then(({ data }) => setReviews(data));
  };

  const handleClose = () => {
    setOpen(false);
  };

  // useEffect(() => {
  //   axios.get(`/api/review/${id}`).then(({ data }) => setReviews(data));
  // }, []);

  const history = useHistory();
  console.log(id);
  //const classes = useStyles();
  const dispatch = useDispatch();

  // const history = useHistory();

  const libro = useSelector((store) => store.book);
  const carrito = useSelector((store) => store.carrito);

  useEffect(() => {
    dispatch(setBook(id));
    localStorage.setItem("book", JSON.stringify(carrito));
  }, [carrito]);

  const handleClick = (libro) => {
    dispatch(setCarrito(libro));
    let carritoCompras = libro;
    let userId = localStorage.getItem("userId");
    dispatch(setCarritoLogin({ userId, carritoCompras }));
  };

  return (
    <>
      <div className={classes.root}>
        <div style={{ marginTop: "50px" }}>
          <Paper className={classes.paper}>
            <Typography gutterBottom variant="h3" align="center">
              {libro.titulo}
            </Typography>
            <Grid container>
              <Grid container style={{ width: 500 }} justify="space-around">
                <ButtonBase className={classes.image}>
                  <img
                    className={classes.img}
                    alt="complex"
                    src={libro.imagen}
                  />
                </ButtonBase>
              </Grid>
              <Grid item xs={12} sm container>
                <Grid item xs container direction="column" spacing={2}>
                  <Grid item xs>
                    <Typography variant="body2" gutterBottom>
                      {libro.reseqna}
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                      Autor: {libro.autor}
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                      $ {libro.precio}
                    </Typography>

                    <Typography variant="body2" color="textSecondary">
                      Categoria:
                      {(libro.categoria && libro.categoria.name) ||
                        " Sin categoría"}
                    </Typography>
                  </Grid>

                  <ButtonGroup
                    variant="text"
                    color="primary"
                    aria-label="text primary button group"
                  >
                    <Button onClick={() => handleClick(libro)}>
                      Agregar a carrito
                    </Button>
                    <Button
                      onClick={() => history.push(`/singlecard/${id}/review`)}
                    >
                      Agregar review
                    </Button>
                    <Button onClick={handleClickOpen}>ver reviews</Button>
                  </ButtonGroup>
                </Grid>
              </Grid>
            </Grid>
          </Paper>
        </div>
      </div>
      <Dialog
        fullScreen
        open={open}
        onClose={handleClose}
        TransitionComponent={Transition}
      >
        <AppBar className={classes.appBar}>
          <Toolbar>
            <IconButton
              edge="start"
              color="inherit"
              onClick={handleClose}
              aria-label="close"
            >
              <CloseIcon />
            </IconButton>
            <Typography variant="h6" className={classes.title}>
              Reviews
            </Typography>
          </Toolbar>
        </AppBar>
        <List>
          {reviews &&
            reviews.map((review, i) => (
              <ListItem button>
                <ListItemText
                  primary={review.texto}
                  secondary={review.user.nombre}
                />
                <Divider />
              </ListItem>
            ))}
        </List>
      </Dialog>
    </>
  );
}
