import React, { useEffect } from "react";

import { Button, TextField, Grid } from "@material-ui/core";
import { setBook } from "../state/book";
import { useSelector, useDispatch } from "react-redux";

import useStyles from "../utils/stylesRegister";

export default function BookEdit({ id }) {
  const book = useSelector((state) => state.book);

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(setBook(id));
  }, []);

  const classes = useStyles();
  return (
    <form className={classes.form} /*onSumbit={onSubmit}*/>
      <Grid container spacing={2}>
        <Grid item xs={12} sm={6}>
          <TextField
            /*   onChange={handleChange} */
            autoComplete="fname"
            name="nombre"
            variant="outlined"
            required
            fullWidth
            id="firstName"
            label="Titulo"
            autoFocus
            defaultValue={book.titulo}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            /*     onChange={handleChange} */
            variant="outlined"
            required
            fullWidth
            id="lastName"
            label="Autor"
            name="apellido"
            autoComplete=""
            defaultValue={book.autor}
          />
        </Grid>

        <InputLabel htmlFor="outlined-age-native-simple">Age</InputLabel>
        <Select
          native
          value={state.age}
          onChange={handleChange}
          label="Age"
          inputProps={{
            name: "age",
            id: "outlined-age-native-simple",
          }}
        >
          <option aria-label="None" value="" />
          <option value={10}>Ten</option>
          <option value={20}>Twenty</option>
          <option value={30}>Thirty</option>
        </Select>

        <Grid item xs={12}>
          <TextField
            A
            variant="outlined"
            required
            fullWidth
            id="email"
            label="Editorial"
            name="email"
            autoComplete="email"
            defaultValue={book.editorial}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            /*   onChange={handleChange} */
            variant="outlined"
            required
            fullWidth
            name="password"
            label="Precio"
            id="password"
            autoComplete="current-password"
            defaultValue={book.precio}
          />
        </Grid>
      </Grid>
      <Button
        type="submit"
        fullWidth
        variant="contained"
        color="primary"
        className={classes.submit}
      >
        Enviar Info
      </Button>
    </form>
  );
}
