import { createReducer, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

export const setCategoria = createAsyncThunk("SET_CATEGORIA", (id) => {
  return axios.get(`/api/categoria/${id}`).then((res) => res.data);
});

export const categoriaReducer = createReducer([], {
  [setCategoria.fulfilled]: (state, action) => action.payload,
});
