import React, { useEffect, useState } from "react";
import axios from "axios";

import {
  FormControl,
  Grid,
  InputLabel,
  MenuItem,
  Select,
  TextField,
  Typography,
  Button,
} from "@material-ui/core";

import { setBook } from "../state/book";
import { useSelector, useDispatch } from "react-redux";
import { setCategorias } from "../state/categorias";
import { setBooks } from "../state/books";

import useStyles from "../utils/stylesRegister";
import { useHistory, useLocation } from "react-router";

export default function EditContainer({ id, valor }) {
  const [input, setInput] = useState({});

  const param = valor;

  /*const handleSubmit = (e) => {
    console.log("click");
    e.preventDefault();
    axios
      .post("http://localhost:5000/api/login", input)
      .then((res) => {
        localStorage.setItem("user", res.data.user.firstName);
        localStorage.setItem("token", res.data.token);
      })
      .then((e) => alert("logueado"))
      .then((redirect) => history.push("/home"))
      .catch((err) => console.log(err));
  }; */

  const history = useHistory();
  const book = useSelector((store) => store.book);
  const [titulo, setTitulo] = useState(book.titulo);
  const [autor, setAutor] = useState(book.autor);
  const [editorial, setEditorial] = useState(book.editorial);
  const [precio, setPrecio] = useState(book.precio);
  const categorias = useSelector((state) => state.categorias);

  const [selected, setSelected] = useState();
  const [imagen, setImgURL] = useState(book.imagen);
  const [categoria, setCategoria] = useState();
  const [num_paginas, setNumPaginas] = useState(book.num_paginas);
  const [edicion, setEdicion] = useState(book.edicion);
  const [reseqna, setReseqna] = useState(book.reseqna);
  const [open, setOpen] = useState(false);

  const dispatch = useDispatch();
  const location = useLocation();

  useEffect(() => {
    dispatch(setCategorias());
    dispatch(setBook(id));
  }, [selected]);

  const handleSelected = (name, id) => {
    setSelected(name);
    setCategoria(id);
  };

  const handleClick = () => {
    setOpen(true);
  };

  const action =
    location.pathname === "/admin/create/:id"
      ? `/api/admin/products/create`
      : `/api/admin/products/edit/${id}`;

  const propiedades = {
    titulo,
    num_paginas,
    edicion,
    reseqna,
    categoria,
    autor,
    editorial,
    precio,
    imagen,
  };

  const handleChange = (e) => {
    if (e.target.name === "titulo") setTitulo(e.target.value);
    if (e.target.name === "autor") setAutor(e.target.value);
    if (e.target.name === "imagen") setImgURL(e.target.value);
    if (e.target.name === "editorial") setEditorial(e.target.value);
    if (e.target.name === "precio") setPrecio(e.target.value);
    if (e.target.name === "num_pag") setNumPaginas(e.target.value);
    if (e.target.name === "edicion") setEdicion(e.target.value);
    if (e.target.name === "reseqna") setReseqna(e.target.value);
  };

  /*  const handleChange = (e) => {
    const key = e.target.name;
    const value = e.target.value;
    setInput({ ...input, [key]: value });
  }; */

  const onSubmit = (e) => {
    e.preventDefault();
    return axios
      .post(action, propiedades)
      .then(() => handleClick())
      .then(() => {
        dispatch(setBooks())
          .then(() => {
            alert("Libro creado o actualizado");
          })
          .then(() => setTimeout(() => history.push("/admin/products"), 2000));
      });
  };

  const classes = useStyles();

  return (
    <form className={classes.form} onSubmit={onSubmit}>
      <Typography variant="h4" style={{ margin: 20 }}>
        {param} un libro
      </Typography>
      <Grid
        container
        spacing={4}
        container
        direction="row"
        justify="space-between"
        alignItems="center"
      >
        <Grid item xs={12}>
          <TextField
            name="titulo"
            label="Título"
            variant="outlined"
            fullWidth
            id="firstName"
            placeholder={"Título: " + book && book.titulo}
            autoFocus
            value={input.titulo}
            onChange={handleChange}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            variant="outlined"
            label="Autor"
            fullWidth
            id="lastName"
            placeholder={"Autor: " + book && book.autor}
            name="autor"
            value={input.autor}
            onChange={handleChange}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <FormControl
            variant="filled"
            className={classes.formControl}
            fullWidth
          >
            <InputLabel id="demo-simple-select-filled-label">
              {(selected && selected) ||
                (book.categoria && book.categoria.name) ||
                "Categoría"}
            </InputLabel>
            <Select
              labelId="demo-simple-select-filled-label"
              name="categoria"
              id="demo-simple-select-filled"
              value={selected}
              onChange={handleChange}
            >
              {categorias &&
                categorias.map((categ) => {
                  <MenuItem value="">
                    <em>{selected && selected}</em>
                  </MenuItem>;
                  return (
                    <MenuItem
                      value={selected}
                      onClick={() => handleSelected(categ.name, categ._id)}
                    >
                      {categ.name}
                    </MenuItem>
                  );
                })}
            </Select>
          </FormControl>
        </Grid>

        <Grid item xs={12} sm={6}>
          <TextField
            name="editorial"
            variant="outlined"
            fullWidth
            id="editorial"
            placeholder={"Editorial: " + book && book.editorial}
            label="Editorial"
            autoComplete="email"
            onChange={handleChange}
            value={input.editorial}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            name="edicion"
            variant="outlined"
            fullWidth
            id="edición"
            label={"Edición: "}
            autoComplete="email"
            onChange={handleChange}
            value={input.edicion}
          />
        </Grid>
        <Grid Item xs={12} style={{ padding: 20 }}>
          <TextField
            variant="outlined"
            placeholder={"Reseña:" + book && book.reseqna}
            fullWidth
            label={"Reseña:"}
            multiline
            rows={6}
            value={input.resegna}
          />
        </Grid>
        <Grid item xs={6} sm={3} justify="center">
          <TextField
            name="precio"
            variant="outlined"
            placeholder={"Precio: $" + book && book.precio}
            label="Precio"
            margin="normal"
            id="precio"
            autoComplete="current-password"
            value={input.precio}
            onChange={handleChange}
          />
        </Grid>
      </Grid>
      <Grid
        container
        spacing={4}
        container
        direction="row"
        justify="center"
        alignItems="center"
      >
        <Grid item xs={6} sm={3} justify="center">
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="secondary"
            className={classes.submit}
          >
            Enviar Info
          </Button>
        </Grid>
      </Grid>
    </form>
  );
}
