import React, { useEffect, useState } from "react";
import axios from "axios";

import {
  Button,
  TextField,
  Grid,
  InputLabel,
  Select,
  Typography,
} from "@material-ui/core";
import { setBook } from "../state/book";
import { useSelector, useDispatch } from "react-redux";
import { setCategorias } from "../state/categorias";

import useStyles from "../utils/stylesRegister";

export default function BookEdit({ id }) {
  /*   const book = useSelector((state) => state.book); */
  //const [book, setBook] = useState("");
  const book = useSelector((store) => store.book);
  const [titulo, setTitulo] = useState("");
  const [autor, setAutor] = useState("");
  const [editorial, setEditorial] = useState("");
  const [precio, setPrecio] = useState("");

  const categorias = useSelector((state) => state.categorias);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(setCategorias());
  }, []);

  useEffect(() => {
    dispatch(setBook(id));
  }, []);

  const handleChange = (e) => {
    if (e.target.name === "titulo") setTitulo(e.target.value);
    if (e.target.name === "autor") setAutor(e.target.value);
    if (e.target.name === "editorial") setEditorial(e.target.value);
    if (e.target.name === "precio") setPrecio(e.target.value);
  };

  const onSubmit = (e) => {
    console.log(editorial);
    e.preventDefault();
    // return axios.put(`/api/products/${id}`, { titulo, autor, editorial, precio });
  };

  const classes = useStyles();

  return (
    <form className={classes.form} onSubmit={onSubmit}>
      <Grid
        container
        spacing={2}
        container
        direction="row"
        justify="space-around"
        alignItems="center"
      >
        <Grid item xs={12} sm={6}>
          <TextField
            name="titulo"
            variant="outlined"
            fullWidth
            id="firstName"
            label={"Título: " + book.titulo}
            autoFocus
            value={titulo}
            onChange={handleChange}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            variant="outlined"
            fullWidth
            id="lastName"
            label={"Autor: " + book.autor}
            name="autor"
            value={autor}
          />
        </Grid>
        <Grid Item>
          <InputLabel htmlFor="outlined-age-native-simple">
            Categoria
          </InputLabel>
          <Select
            native
            value=""
            label="Categoria"
            inputProps={{
              name: "age",
              id: "outlined-age-native-simple",
            }}
          >
            <option aria-label="None" value="" />
            {categorias &&
              categorias.map((categoria) => {
                return <option value={categoria.name}>{categoria.name}</option>;
              })}
          </Select>
        </Grid>
        <Grid item xs={12}>
          <TextField
            name="editorial"
            variant="outlined"
            fullWidth
            id="editorial"
            label={"Editorial: " + book.editorial}

            autoComplete="email"
            onChange={handleChange}
            value={editorial}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            name="precio"
            variant="outlined"
            fullWidth
            label="Precio"
            id="password"
            autoComplete="current-password"
            value={book.precio}
          />
        </Grid>
      </Grid>
      <Button
        type="submit"
        fullWidth
        variant="contained"
        color="primary"
        className={classes.submit}
      >
        Enviar Info
      </Button>
    </form>
  );
}
