  }, []);

  const handleChange = (e) => {
    setInput(e.target.value)
  }

  console.log(input)

  const handleSubmit = (e) => {
    e.preventDefault()
    console.log(categoria)
  }

  //axios.put(`/api/admin/categoria/edit/${id}`)

  return (
    <div>
      <form onSubmit={handleSubmit}>
