import React, { useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";
import { setUser } from "../state/user";
import { useDispatch } from "react-redux";
import Login from "../components/Login";
import NavBar from "../components/Navbar";

const FormLoginContainer = () => {
  const [titulo, setTitulo] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const dispatch = useDispatch();
  const history = useHistory();

  const handleChange = (e) => {
    if (e.target.name === "titulo") setTitulo(e.target.value);
    if (e.target.name === "email") setEmail(e.target.value);
    if (e.target.name === "password") setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    return axios.post(`/api/login`, { email, password }).then((user) => {
      localStorage.setItem("token", user.data.token);
      localStorage.setItem("user", user.data.user.nombre);
      dispatch(setUser(user.data));
      return history.push("/");
    });
  };

  return (
    <div>
      <Login handleChange={handleChange} handleSubmit={handleSubmit} />
      <NavBar handleChange={handleChange} />
    </div>
  );
};

export default FormLoginContainer;
