import React, { useEffect, useState } from "react";
import axios from "axios";

import {
  Button,
  TextField,
  Grid,
  InputLabel,
  Select,
  Typography,
} from "@material-ui/core";
import { setBook } from "../state/book";
import { useSelector, useDispatch } from "react-redux";
import { setCategorias } from "../state/categorias";

import useStyles from "../utils/stylesRegister";

export default function BookEdit({ id }) {
  /*   const book = useSelector((state) => state.book); */
  //const [book, setBook] = useState("");
  const book = useSelector((store) => store.book);

  const categorias = useSelector((state) => state.categorias);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(setCategorias());
  }, []);

  useEffect(() => {
    dispatch(setBook(id));
  }, []);

  /*   const onSubmit = (e) => {
    e.preventDefault();
    axios.put(`/api/products/${id}`, {});
  }; */

  const classes = useStyles();

  return (
    <form className={classes.form} /*onSumbit={onSubmit}*/>
      <Grid
        container
        spacing={2}
        container
        direction="row"
        justify="space-around"
        alignItems="center"
      >
        <Grid item xs={12} sm={6}>
          <TextField
            /*   onChange={handleChange} */
            autoComplete="fname"
            name="nombre"
            variant="outlined"
            required
            fullWidth
            id="firstName"
            label="Titulo"
            autoFocus
            value={book.titulo}
          />
          <Typography variant="body2" gutterBottom>
            {book.reseqna}
          </Typography>
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            /*     onChange={handleChange} */
            variant="outlined"
            required
            fullWidth
            id="lastName"
            label="Autor"
            name="apellido"
            autoComplete=""
            value={book.autor}
          />
        </Grid>
        <Grid Item>
          <InputLabel htmlFor="outlined-age-native-simple">
            Categoria
          </InputLabel>
          <Select
            native
            value=""
            /*   onChange={handleChange} */
            label="Categoria"
            inputProps={{
              name: "age",
              id: "outlined-age-native-simple",
            }}
          >
            <option aria-label="None" value="" />
            {categorias &&
              categorias.map((categoria) => {
                return <option value={categoria.name}>{categoria.name}</option>;
              })}
          </Select>
        </Grid>
        <Grid item xs={12}>
          <Typography>Editorial</Typography>
          <TextField
            A
            variant="outlined"
            required
            fullWidth
            id="email"
            placeholder={book.editorial}
            name="email"
            autoComplete="email"
            value=""
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            /*   onChange={handleChange} */
            variant="outlined"
            required
            fullWidth
            name="password"
            label="Precio"
            id="password"
            autoComplete="current-password"
            value={book.precio}
          />
        </Grid>
      </Grid>
      <Button
        type="submit"
        fullWidth
        variant="contained"
        color="primary"
        className={classes.submit}
      >
        Enviar Info
      </Button>
    </form>
  );
}
