import axios from "axios";
import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { setCategorias } from '../state/categorias'
export default function CategoriaEdit({ id }) {

    const dispatch = useDispatch() 
    const [categoria, setCategoria] = useState("")
    const [input, setInput] = useState("")

    useEffect(() => {
    axios.get(`/api/admin/categoria/${id}`)
    .then(({data}) => setCategoria(data.name))
  }, []);

  const handleChange = (e) => {
    const value = e.target.value 
    setInput(value)
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    axios.post(`/api/admin/categoria/edit/${id}`, {name: input})
    .then(categoria => dispatch(setCategorias(categoria)))
    setInput("")
  }

  //axios.put(`/api/admin/categoria/edit/${id}`)

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder={categoria} value={input} onChange={handleChange}/>
      </form>
    </div>
  );
}
