import React, { useEffect, useState } from "react";
import axios from "axios";

import { Button, TextField, Grid, Typography } from "@material-ui/core";
import { setBook } from "../state/book";
import { useSelector, useDispatch } from "react-redux";
import { setCategorias } from "../state/categorias";

import useStyles from "../utils/stylesRegister";
import { useHistory } from "react-router";

export default function CreateBook({ id }) {
  const history = useHistory();
  const book = useSelector((store) => store.book);
  const [titulo, setTitulo] = useState(book.titulo);
  const [autor, setAutor] = useState(book.autor);
  const [editorial, setEditorial] = useState(book.editorial);
  const [precio, setPrecio] = useState(book.precio);
  const [num_paginas, setNumPaginas] = useState(book.num_paginas);
  const [edicion, setEdicion] = useState(book.edicion);
  const [resegna, setResegna] = useState(book.resegna);

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(setCategorias());
    dispatch(setBook(id));
  }, []);

  const handleChange = (e) => {
    if (e.target.name === "titulo") setTitulo(e.target.value);
    if (e.target.name === "autor") setAutor(e.target.value);
    if (e.target.name === "editorial") setEditorial(e.target.value);
    if (e.target.name === "precio") setPrecio(e.target.value);
    if (e.target.name === "num_pag") setNumPaginas(e.target.value);
    if (e.target.name === "edicion") setEdicion(e.target.value);
    if (e.target.name === "resegna") setResegna(e.target.value);
  };

  const onSubmit = (e) => {
    e.preventDefault();
    axios
      .post(`/api/admin/products/create`, {
        titulo,
        num_paginas,
        edicion,
        resegna,
        autor,
        editorial,
        precio,
      })
      .then(() => {
        alert("Libro Creado");
        history.push("/admin/products");
      });
  };

  const classes = useStyles();

  return (
    <form className={classes.form} onSubmit={onSubmit}>
      <Typography variant="h4" style={{ margin: 20 }}>
        Modificá el libro
      </Typography>
      <Grid
        container
        spacing={4}
        container
        direction="row"
        justify="space-between"
        alignItems="center"
      >
        <Grid item xs={12}>
          <TextField
            name="titulo"
            label=""
            variant="outlined"
            fullWidth
            id="firstName"
            label={"Título: "}
            autoFocus
            value={titulo}
            onChange={handleChange}
          />
        </Grid>
        {/* <Grid Item>
              <InputLabel htmlFor="outlined-age-native-simple">
                Categoria
              </InputLabel>
              <Select
                native
                value=""
                label="Categoria"
                inputProps={{
                  name: "age",
                  id: "outlined-age-native-simple",
                }}
              >
                <option aria-label="None" value="" />
                {categorias &&
                  categorias.map((categoria) => {
                    return <option value={categoria.name}>{categoria.name}</option>;
                  })}
              </Select>
            </Grid> */}
        <Grid item xs={12} sm={6}>
          <TextField
            variant="outlined"
            fullWidth
            id="lastName"
            label={"Autor: "}
            name="autor"
            value={autor}
            onChange={handleChange}
          />
        </Grid>

        <Grid item xs={12} sm={6}>
          <TextField
            name="editorial"
            variant="outlined"
            fullWidth
            id="editorial"
            label={"Editorial: "}
            autoComplete="email"
            onChange={handleChange}
            value={editorial}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            variant="outlined"
            fullWidth
            id="lastName"
            label={"Num de pag: "}
            name="num_pag"
            value={num_paginas}
            onChange={handleChange}
          />
        </Grid>

        <Grid item xs={12} sm={6}>
          <TextField
            name="edicion"
            variant="outlined"
            fullWidth
            id="edición"
            label={"Edición: "}
            autoComplete="email"
            onChange={handleChange}
            value={edicion}
          />
        </Grid>
        <Grid Item xs={12} style={{ padding: 20 }}>
          <TextField
            variant="outlined"
            name="resegna"
            placeholder={"Reseña:"}
            fullWidth
            label={"Reseña:"}
            multiline
            rows={6}
          />
        </Grid>
        <Grid item xs={6} sm={3} justify="center">
          <TextField
            name="precio"
            variant="outlined"
            label={"Precio: $"}
            margin="normal"
            id="precio"
            autoComplete="current-password"
            value={precio}
            onChange={handleChange}
          />
        </Grid>
      </Grid>
      <Grid
        container
        spacing={4}
        container
        direction="row"
        justify="center"
        alignItems="center"
      >
        <Grid item xs={6} sm={3} justify="center">
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="secondary"
            className={classes.submit}
          >
            Crear Libro
          </Button>
        </Grid>
      </Grid>
    </form>
  );
}
