import React, { useEffect, useState } from "react";
import axios from "axios";
import { useSelector, useDispatch } from "react-redux";
import {
  Avatar,
  Grid,
  IconButton,
  List,
  ListItem,
  ListItemAvatar,
  ListItemSecondaryAction,
  ListItemText,
  Typography,
} from "@material-ui/core";
import { setBooks } from "../state/books";
import { setBook } from "../state/book";

import { Folder, Edit, Delete } from "@material-ui/icons";
import { useStyles } from "../utils/stylesAdminUsers";
import BookEdit from "./BookEdit";

export default function AdminBooks() {
  const books = useSelector((state) => state.books);
  /*   const book = useSelector((state) => state.book); */
  const [book, setBook] = useState("");

  const dispatch = useDispatch();
  const classes = useStyles();

  useEffect(() => {
    dispatch(setBooks());
  }, []);

  useEffect(() => {
    setBooks(book);
  }, [book]);

  const handleDelete = (id) => {
    axios.delete(`/api/admin/products/${id}`);
    dispatch(setBooks());
  };

  /*   const handleEditBook = (book) => {
    setBook(book);

  }; */

  const handleEditBook = (id) => {
    return history.push(`/admin/products/edit/${id}`);
  };

  return (
    <div>
      <Grid
        container
        direction="row"
        justify="space-around"
        alignItems="center"
      >
        <Grid item xs={12} md={6} style={{ paddingLeft: 20 }}>
          <Typography variant="h6" className={classes.title}>
            Users
          </Typography>
          <div className={classes.demo}>
            <List>
              {books &&
                books.map((book, i) => (
                  <ListItem key={i}>
                    <ListItemAvatar>
                      <Avatar>
                        <Folder />
                      </Avatar>
                    </ListItemAvatar>
                    <ListItemText primary={book.titulo} />
                    <ListItemSecondaryAction
                      style={{ paddingRight: 65 }}
                      onClick={() => handleEditBook(book)}
                    >
                      <IconButton edge="end" aria-label="add-admin">
                        <Edit />
                      </IconButton>
                    </ListItemSecondaryAction>
                    <ListItemSecondaryAction
                      onClick={() => handleDelete(book._id)}
                    >
                      <IconButton edge="end" aria-label="delete">
                        <Delete />
                      </IconButton>
                    </ListItemSecondaryAction>
                  </ListItem>
                ))}
            </List>
          </div>
        </Grid>
        <Grid Item>{book._id ? <BookEdit book={book} /> : null}</Grid>
      </Grid>
    </div>
  );
}
