import React, { useEffect, useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import {
  Avatar,
  Grid,
  IconButton,
  List,
  ListItem,
  ListItemAvatar,
  ListItemSecondaryAction,
  ListItemText,
  Typography,
  Tooltip,
} from "@material-ui/core";
import { setBooks } from "../state/books";
import { setBook } from "../state/book";

import { Edit, Delete } from "@material-ui/icons";
import MenuBookIcon from "@material-ui/icons/MenuBook";
import { useStyles } from "../utils/stylesAdminUsers";
import BookEdit from "./BookEdit";

export default function AdminBooks() {
  const books = useSelector((state) => state.books);
  /*   const book = useSelector((state) => state.book); */
  const [book] = useState("");

  const history = useHistory();

  const dispatch = useDispatch();
  const classes = useStyles();

  useEffect(() => {
    dispatch(setBooks());
    dispatch(setBook());
  }, []);

  const handleDelete = (id) => {
    axios.delete(`/api/admin/products/${id}`);
    dispatch(setBooks());
  };

  const handleEditBook = (id) => {
    return history.push(`/admin/product/edit/${id}`);
  };

  return (
    <div>
      <Grid
        container
        direction="row"
        justify="space-around"
        alignItems="center"
      >
        <Grid item xs={12} md={6} style={{ paddingLeft: 20 }}>
          <Typography variant="h6" className={classes.title}>
            Libros (TAREA PENDIENTE - AGREGAR LIBROS!!!)
          </Typography>
          <div className={classes.demo}>
            <List>
              {books &&
                books.map((book, i) => (
                  <ListItem key={i}>
                    <ListItemAvatar>
                      <Avatar>
                        <MenuBookIcon />
                      </Avatar>
                    </ListItemAvatar>

                    <ListItemText
                      primary={book.titulo}
                      onClick={handleEditBook}
                    />
                    <Tooltip title="Editar">
                      <ListItemSecondaryAction
                        style={{ paddingRight: 65 }}
                        onClick={() => handleEditBook(book._id)}
                      >
                        <IconButton edge="end" aria-label="add-admin">
                          <Edit />
                        </IconButton>
                      </ListItemSecondaryAction>
                    </Tooltip>
                    <Tooltip title="Eliminar">
                      <ListItemSecondaryAction
                        onClick={() => handleDelete(book._id)}
                      >
                        <IconButton edge="end" aria-label="delete">
                          <Delete />
                        </IconButton>
                      </ListItemSecondaryAction>
                    </Tooltip>
                  </ListItem>
                ))}
            </List>
          </div>
        </Grid>
        <Grid Item>{book._id ? <BookEdit book={book} /> : null}</Grid>
      </Grid>
    </div>
  );
}
